"use strict";const gameBoard=(()=>{let e=Array(9).fill("");const t=t=>{return e[t]!==""?true:false};return{contents:e,isNotEmptyAtIndex:t}})();const displayController=(()=>{const e=()=>{let e=gameBoard.contents;for(let t=0;t<e.length;t++){let r=document.querySelector(`[data-board-index="${t}"]`);r.innerHTML=e[t]}};return{displayGameBoardContents:e}})();const playerFactory=e=>{return{symbol:e}};const gameFactory=()=>{const e=[...document.querySelectorAll(".square")];let t=playerFactory("X");let r=playerFactory("O");let o=1;const a=()=>{e.forEach(e=>{e.onclick=(()=>{n(e)})})};const n=e=>{let a=e.getAttribute("data-board-index");if(gameBoard.isNotEmptyAtIndex(a)){return}else{if(l(o)){gameBoard.contents[a]=r.symbol}else{gameBoard.contents[a]=t.symbol}o++;displayController.displayGameBoardContents()}};const l=e=>{return e%2===0?true:false};return{loadSquares:a}};let game=gameFactory();game.loadSquares();